import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom'; // Import Routes and Route
import Login from './components/Login';  // Separate Login component
import Signup from './components/Signup';  // Separate Signup component
import Task from './components/Task';
import Project from './components/Project';
import './index.css';

const App = () => {
    const [token, setToken] = useState(null);  // Token state for authentication

    return (
        <div>
            <Routes>
                {/* Route for the main page (dashboard) */}
                <Route path="/" element={token ? (
                    <>
                        <Task token={token} />
                        <Project token={token} />
                    </>
                ) : (
                    <Login setToken={setToken} />  // Redirect to Login if not authenticated
                )} />
                
                {/* Route for Login */}
                <Route path="/login" element={<Login setToken={setToken} />} />
                
                {/* Route for Signup */}
                <Route path="/signup" element={<Signup setToken={setToken} />} />
                
                {/* Additional routes can be added here */}
            </Routes>
        </div>
    );
};

export default App;
