import React, { useState } from 'react';
import { Routes, Route, BrowserRouter as Router } from 'react-router-dom'; // Import BrowserRouter
import Auth from './components/Auth';
import Task from './components/Task';
import Project from './components/Project'; // Import Project component
import './index.css';

const App = () => {
    const [token, setToken] = useState(localStorage.getItem('token') || null); // Check token in localStorage

    // Handle logout
    const logout = () => {
        setToken(null);
        localStorage.removeItem('token'); // Optionally remove token from localStorage
    };

    return (
        <Router>
            <div>
                <Routes>
                    <Route path="/" element={token ? (
                        <>
                            <Task token={token} />
                            <Project token={token} /> {/* Include Project component */}
                            <button onClick={logout}>Logout</button> {/* Logout button */}
                        </>
                    ) : (
                        <Auth setToken={setToken} />
                    )} />
                    <Route path="/login" element={<Auth setToken={setToken} />} />
                    <Route path="/signup" element={<Auth setToken={setToken} />} />
                    {/* Add other routes as needed */}
                </Routes>
            </div>
        </Router>
    );
};

export default App;
